import {
  Avatar,
  CardContent,
  Divider,
  Grid,
  Typography,
  IconButton,
} from "@material-ui/core";
import { Card, CardHeader } from "@material-ui/core";
import React from "react";
import CloseIcon from "@material-ui/icons/Close";
const UserDetail = ({ label, detail }) => {
  return (
    <>
      <Grid container spacing={2}>
        <Grid item xs={6} style={{ padding: "16px" }}>
          <Typography
            variant={"body2"}
            color="text.primary"
            style={{
              textAlign: "left",
              fontWeight: "bold",
              paddingLeft: "16px",
            }}
          >
            {label}
          </Typography>
        </Grid>
        <Grid item xs={6} style={{ padding: "16px" }}>
          <Typography
            variant={"body2"}
            color="text.secondary"
            style={{ textAlign: "left" }}
          >
            {detail}
          </Typography>
        </Grid>
      </Grid>
      <Divider />
    </>
  );
};
const UserCard = ({ person, handleClose }) => {
  return (
    <Card style={{ maxWidth: "500px" }}>
      <CardHeader
        avatar={
          <Avatar
            alt={`${person.first_name} ${person.last_name}`}
            src={person.avatar}
          />
        }
        title={`${person.first_name} ${person.last_name}`}
        subheader={`${person.email}`}
        action={
          <IconButton aria-label="settings" onClick={handleClose}>
            <CloseIcon />
          </IconButton>
        }
      />
      <CardContent style={{ padding: "0px" }}>
        <UserDetail label={"First Name"} detail={person.first_name} />
        <UserDetail label={"Last Name"} detail={person.last_name} />
        <UserDetail label={"Job"} detail={person.job} />
        <UserDetail label={"Department"} detail={person.department} />
      </CardContent>
    </Card>
  );
};

export default UserCard;
