import { Grid } from "@material-ui/core";
import React, { Component } from "react";
import Metric from "./Metric";

const MetricRow = ({ data, dateFilter }) => {
  console.log({ dateFilter: dateFilter.value });
  const filteredDate = data.filter((d) => {
    if (d.label === "All Orders") {
      return d;
    } else {
      if (new Date(d.purchase_date) > d.value) {
        return d;
      }
    }
  });
  const pending = data.filter((d) => d.order_status === "Pending");
  const cancelled = data.filter((d) => d.order_status === "Cancelled");
  const completed = data.filter((d) => d.order_status === "Completed");
  const shipped = data.filter((d) => d.order_status === "Shipped");

  return (
    <Grid container>
      <Grid item sm={3} spacing={3} style={{ borderRight: "1px solid #ccc" }}>
        <Metric
          title={"Orders"}
          metric={data.length}
          variance={"+12%"}
          varianceType={"success"}
          subtitle={dateFilter.label}
        />
      </Grid>
      <Grid item sm={3} spacing={3} style={{ borderRight: "1px solid #ccc" }}>
        <Metric
          title={"Shipped"}
          metric={shipped.length}
          variance={"-10%"}
          varianceType={"error"}
          subtitle={dateFilter.label}
        />
      </Grid>
      <Grid item sm={3} style={{ borderRight: "1px solid #ccc" }}>
        <Metric
          title={"Completed"}
          metric={completed.length}
          variance={"+1%"}
          varianceType={"error"}
          subtitle={dateFilter.label}
        />
      </Grid>
      <Grid item sm={3}>
        <Metric
          title={"Pending"}
          metric={pending.length}
          variance={"+1%"}
          varianceType={"error"}
          subtitle={dateFilter.label}
        />
      </Grid>
    </Grid>
  );
};

export default MetricRow;
