import React, { useState } from "react";
import DataTableScene from "../scene/DataTableScene";
import Container from "@material-ui/core/Container";
import Typography from "@material-ui/core/Typography";
import Box from "@material-ui/core/Box";
import Divider from "@material-ui/core/Divider";
import Avatar from "@material-ui/core/Avatar";
import { mockData } from "../data/MockData";
import MetricRow from "../components/main-page-metrics/MetricRow";

const Dashboard = () => {
  const [data, setData] = useState(mockData);
  const [dateFilter, setDateFilter] = useState({
    label: "All Orders",
    value: "",
  });
  const handleDateFilter = (newFilter) => {
    setDateFilter(newFilter);
    let filteredDate = data.filter((d) => {
      // console.log(new Date(d.purchase_date));

      if (dateFilter.label === "All Orders") {
        return d;
      } else if (new Date(d.purchase_date) > new Date(dateFilter.value)) {
        return d;
      } else {
        return;
      }
    });
  };

  return (
    <Container maxWidth="xl">
      <Box padding="32px 16px" style={{ display: "flex" }}>
        <div style={{ margin: "4px 16px 4px 0px" }}>{/* <Avatar /> */}</div>
        <div>
          <Typography variant="h4" style={{ textAlign: "left" }}>
            <span style={{ fontWeight: "bold" }}>Welcome Back, </span> Miranda!
          </Typography>
          <Typography
            variant="h6"
            style={{ textAlign: "left" }}
            color="text.primary"
          >
            Premium user
          </Typography>
        </div>
      </Box>{" "}
      <Divider />
      <Box padding="32px 16px">
        <MetricRow
          data={mockData}
          dateFilter={dateFilter}
          filteredDate={mockData}
        />
      </Box>
      <Divider style={{ marginBottom: "32px" }} />
      <DataTableScene
        mockData={mockData}
        handleDateFilter={handleDateFilter}
        dateFilter={dateFilter}
        setData={setData}
      />
    </Container>
  );
};

export default Dashboard;
