import React, { useState, useEffect } from "react";
import DataTable from "../components/table/DataTable";
import { mockData, defaultColumns } from "../data/MockData";
import Box from "@material-ui/core/Box";
import _ from "lodash";
import IconInput from "../components/inputs/IconInput";
import SlideInDialog from "../components/dialog/SlideInDialog";
import SearchIcon from "@material-ui/icons/Search";
import { paginate } from "../utils/paginate";
import UserCard from "../components/card/UserCard";
import { Typography } from "@material-ui/core";
const DataTableScene = () => {
  const [data, setData] = useState([]);
  const [columns, setColumns] = useState(defaultColumns);
  const [sortColumn, setSortColumn] = useState({
    path: "first_name",
    order: "asc",
  });
  const [searchQuery, setSearchQuery] = useState("");
  const [pageSize, setPageSize] = useState(10);
  const [totalLength, setLength] = useState(mockData.length);
  const [currentPage, setCurrentPage] = useState(1);
  const [selectedPerson, setSelectedPerson] = useState(null);
  const handleSortColumn = (sortCol) => {
    setSortColumn({ path: sortCol.path, order: sortCol.order });
  };
  const handleSearch = (e) => {
    const value = e.target.value;
    if (value === "") {
      setData(mockData);
      setCurrentPage(1);
    }
    setSearchQuery(value);
  };
  const handleChangePage = (e, newPage) => {
    setCurrentPage(newPage);
  };
  const handleChangeRowsPerPage = (e) => {
    setPageSize(e.target.value);
    setCurrentPage(1);
  };
  const handleSelectedPerson = (person) => {
    setSelectedPerson(person);
  };
  useEffect(() => {
    let filtered = [...mockData];
    const search = new RegExp(searchQuery, "igm");
    if (searchQuery !== "") {
      filtered = filtered.filter(
        (people) =>
          people.first_name.match(search) ||
          people.last_name.match(search) ||
          people.email.match(search) ||
          people.city.match(search) ||
          people.state.match(search)
      );
    }
    const sorted = _.orderBy(filtered, [sortColumn.path], [sortColumn.order]);
    // const people = paginate(sorted, currentPage, pageSize);
    setData([...sorted]);
    setLength(sorted.length);
    // setLength(sorted.length);
  }, [searchQuery, sortColumn, pageSize, currentPage]);

  return (
    <>
      <Box margin="16px">
        <div style={{ display: "flex", justifyContent: "space-between" }}>
          <Typography
            variant="h6"
            style={{ padding: "10px", fontWeight: "bold" }}
          >
            Data
          </Typography>
          <IconInput
            id={"search-data-table"}
            label={"Search"}
            value={searchQuery}
            iconPosition={"start"}
            iconButtonLabel={"search"}
            icon={<SearchIcon />}
            handleChange={handleSearch}
          />
        </div>

        <DataTable
          data={data}
          columns={columns}
          pageSize={pageSize}
          totalLength={totalLength}
          currentPage={currentPage}
          handleChangePage={handleChangePage}
          handleChangeRowsPerPage={handleChangeRowsPerPage}
          sortColumn={sortColumn}
          handleSort={handleSortColumn}
          handleRowClick={handleSelectedPerson}
        />
      </Box>
      <SlideInDialog isOpen={selectedPerson !== null}>
        <UserCard
          person={selectedPerson !== null && selectedPerson}
          handleClose={() => setSelectedPerson(null)}
        />
      </SlideInDialog>
    </>
  );
};

export default DataTableScene;
