import React, { useState, useEffect } from "react";
import DataTable from "../components/table/DataTable";
import { mockData, tableColumns } from "../data/MockData";
import Box from "@material-ui/core/Box";
import _ from "lodash";
import IconInput from "../components/inputs/IconInput";
import SearchIcon from "@material-ui/icons/Search";
const DataTableScene = () => {
  const [data, setData] = useState(mockData);
  const [columns, setColumns] = useState(tableColumns);
  const [sortColumn, setSortColumn] = useState({
    path: "first_name",
    order: "asc",
  });
  const [searchQuery, setSearchQuery] = useState(null);
  const handleSortColumn = (sortCol) => {
    setSortColumn({ path: sortCol.path, order: sortCol.order });
  };

  useEffect(() => {
    let filtered = [...data];
    const sorted = _.orderBy(filtered, [sortColumn.path], [sortColumn.order]);
    setData([...sorted]);
  }, [searchQuery, sortColumn]);
  return (
    <Box margin="16px">
      <IconInput
        id={"search-data-table"}
        label={"Search"}
        value={searchQuery}
        iconPosition={"start"}
        iconButtonLabel={"search"}
        icon={<SearchIcon />}
      />
      <DataTable
        data={data}
        columns={columns}
        sortColumn={sortColumn}
        handleSort={handleSortColumn}
      />
    </Box>
  );
};

export default DataTableScene;
