import {
  Avatar,
  CardContent,
  Divider,
  Grid,
  Typography,
  IconButton,
} from "@material-ui/core";
import { Card, CardHeader } from "@material-ui/core";
import React from "react";
import CloseIcon from "@material-ui/icons/Close";

const OrderDetail = ({ user }) => {
  return (
    <React.Fragment>
      <Typography
        variant={"body2"}
        color="text.primary"
        style={{
          textAlign: "left",
          fontWeight: "bold",
        }}
      >
        Ship to
      </Typography>
      <Typography
        variant={"body1"}
        color="text.primary"
        style={{
          textAlign: "left",
        }}
      >
        {user.first_name} {user.last_name}
      </Typography>
      <Typography
        variant={"body2"}
        color="text.primary"
        style={{
          textAlign: "left",
        }}
      >
        {user.address} {user.city}, {user.state}, {user.zip}
      </Typography>
    </React.Fragment>
  );
};
const Products = ({ product, id }) => {
  console.log(product);
  if (!product) {
    return <></>;
  } else {
    return (
      <React.Fragment key={id}>
        <Grid container spacing={2}>
          {/* <Grid item xs={1} style={{ padding: "16px" }}>
            {product.product_image !== null && (
              <Avatar alt={product.product_name} src={product.product_image} />
            )}
          </Grid> */}
          <Grid item xs={8} style={{ padding: "16px" }}>
            <Typography
              variant={"body2"}
              color="text.primary"
              style={{
                textAlign: "left",

                paddingLeft: "16px",
              }}
            >
              {product.product_name}
            </Typography>
          </Grid>
          <Grid item xs={2} style={{ padding: "16px" }}>
            <Typography
              variant={"body2"}
              color="text.secondary"
              style={{ textAlign: "left" }}
            >
              {product.quantity}
            </Typography>
          </Grid>
          <Grid item xs={1} style={{ padding: "16px" }}>
            <Typography
              variant={"body2"}
              color="text.secondary"
              style={{ textAlign: "left" }}
            >
              {product.product_image !== null && "$"}
              {product.product_price}
            </Typography>
          </Grid>
        </Grid>
        <Divider />
      </React.Fragment>
    );
  }
};
const UserCard = ({ order, handleClose }) => {
  const { order: user, products } = order;
  if (products && user) {
    return (
      <Card style={{ maxWidth: "1000px" }}>
        <CardHeader
          title={`Order Details`}
          subheader={`${user.zip}`}
          action={
            <IconButton aria-label="settings" onClick={handleClose}>
              <CloseIcon />
            </IconButton>
          }
        />
        <CardContent style={{ padding: "16px" }}>
          <OrderDetail user={user} />
        </CardContent>
        {/* <CardMedia
        component="img"
        height="194"
        image="/static/images/cards/paella.jpg"
        alt="Paella dish"
      /> */}

        <CardContent style={{ padding: "16px" }}>
          <Products
            product={{
              product_image: null,
              product_name: "Product",
              quantity: "Quantity",
              product_price: "Price",
            }}
            key={products.length + 1}
          />
          {products &&
            products.map((product, index) => (
              <Products product={product} key={index} />
            ))}
        </CardContent>
      </Card>
    );
  } else {
    return null;
  }
};

export default UserCard;
